            JSONObject get = ss.getJSONObject("get");
            if (get != null) {
                System.out.println("GET: " + k + " " + get.getString("summary"));
            }
            JSONObject post = ss.getJSONObject("post");
            if (post != null) {
                System.out.println("POST: " + k + " " + post.getString("summary"));
            }
            JSONObject delete = ss.getJSONObject("delete");
